import React from 'react';

import axios from 'axios';

export default class ProductsList extends React.Component {
  state = {
    products: []
  }

  componentDidMount() {
    axios.get(`http://localhost:3000/products`)
      .then(res => {
        const products = res.data;
        this.setState({ products });
      })
  }

  render() {
    return (
      <div>
        <h1>Liste des produits</h1>
      <table className="table table-bordered " style={{
          color: 'darkviolet'
      }}>
          <thead className="thead-light">
              <tr><th><h3>Produit :</h3></th>
                  <td>{ this.state.products.map(product => <li>{product.name}</li>)}</td>
                  <td>{ this.state.products.map(product => <li>{product.quantity}</li>)}</td>
                  <td>{ this.state.products.map(product => <li>{product.price}</li>)}</td>
              </tr>
          </thead>
      </table>
  </div>
      // <ul>
      //   { this.state.products.map(product => <li>{product.name}</li>)}
      // </ul>
    )
  }
}
